scripts/migrate-devices-to-subcollection.content:
#!/usr/bin/env node
/**
 * Migration script: copy `devices` array from each user document into
 * a `users/{uid}/devices/{deviceId}` subcollection document.
 *
 * Usage:
 *  - Set GOOGLE_APPLICATION_CREDENTIALS to a service account JSON file path, or
 *  - Set FIREBASE_SERVICE_ACCOUNT env var to the service account JSON string
 *
 *  node -r dotenv/config scripts/migrate-devices-to-subcollection.js [--remove-parent]
 *
 *  --remove-parent  : will delete the parent `devices` array after migration
 */

require('dotenv').config();
const admin = require('firebase-admin');
const { v4: uuidv4 } = require('uuid');

function initAdmin() {
  if (process.env.GOOGLE_APPLICATION_CREDENTIALS) {
    console.log('Initializing admin using GOOGLE_APPLICATION_CREDENTIALS file.');
    admin.initializeApp();
    return;
  }

  if (process.env.FIREBASE_SERVICE_ACCOUNT) {
    console.log('Initializing admin using FIREBASE_SERVICE_ACCOUNT env JSON.');
    const svc = JSON.parse(process.env.FIREBASE_SERVICE_ACCOUNT);
    admin.initializeApp({ credential: admin.credential.cert(svc) });
    return;
  }

  console.error('No service account provided. Set GOOGLE_APPLICATION_CREDENTIALS or FIREBASE_SERVICE_ACCOUNT.');
  process.exit(1);
}

async function migrate() {
  initAdmin();
  const db = admin.firestore();
  const removeParent = process.argv.includes('--remove-parent');

  console.log('Fetching users...');
  const usersSnap = await db.collection('users').get();
  console.log(`Found ${usersSnap.size} users.`);

  let migratedCount = 0;
  for (const userDoc of usersSnap.docs) {
    const uid = userDoc.id;
    const data = userDoc.data();
    const devices = data?.devices;
    if (!devices || !Array.isArray(devices) || devices.length === 0) continue;

    console.log(`Migrating ${devices.length} devices for user ${uid}`);
    for (const d of devices) {
      try {
        const id = d?.id || uuidv4();
        const deviceRef = db.collection('users').doc(uid).collection('devices').doc(id);

        // Preserve loggedInAt if present, otherwise set serverTimestamp
        const loggedInAt = d && d.loggedInAt ? d.loggedInAt : admin.firestore.FieldValue.serverTimestamp();

        await deviceRef.set({ ...d, id, loggedInAt }, { merge: true });
        migratedCount++;
      } catch (err) {
        console.error(`Failed to migrate device for user ${uid}:`, err);
      }
    }

    if (removeParent) {
      try {
        await db.collection('users').doc(uid).update({ devices: admin.firestore.FieldValue.delete() });
        console.log(`Removed parent devices array for user ${uid}`);
      } catch (err) {
        console.error(`Failed to remove parent devices array for user ${uid}:`, err);
      }
    }
  }

  console.log(`Migration complete. Created/updated ${migratedCount} device documents.`);
  process.exit(0);
}

migrate().catch((e) => {
  console.error('Migration failed:', e);
  process.exit(1);
});

scripts/migrate-videos-to-cloudinary.js.content:
// migrate-videos-to-cloudinary.js (node)
const admin = require('firebase-admin');
const cloudinary = require('cloudinary').v2;

// Initialize Firebase Admin SDK
// Ensure you have your service account key file and set the GOOGLE_APPLICATION_CREDENTIALS env var
admin.initializeApp({
  credential: admin.credential.applicationDefault(),
  databaseURL: `https://${process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID}.firebaseio.com`
});

// Configure Cloudinary
// Ensure you have these environment variables set in your .env file
cloudinary.config({
  cloud_name: process.env.NEXT_PUBLIC_CLOUDINARY_CLOUD_NAME,
  api_key: process.env.CLOUDINARY_API_KEY,
  api_secret: process.env.CLOUDINARY_API_SECRET
});

const db = admin.firestore();

async function runMigration() {
  console.log('Starting video migration...');

  if (!process.env.NEXT_PUBLIC_CLOUDINARY_CLOUD_NAME || !process.env.CLOUDINARY_API_KEY || !process.env.CLOUDINARY_API_SECRET) {
    console.error('Error: Cloudinary environment variables are not set. Please check your .env file.');
    return;
  }

  const conversationsSnapshot = await db.collection('conversations').get();
  console.log(`Found ${conversationsSnapshot.docs.length} conversations to check.`);

  let migratedCount = 0;
  const migrationPromises = [];

  for (const convoDoc of conversationsSnapshot.docs) {
    const messagesRef = convoDoc.ref.collection('messages');
    const messagesSnapshot = await messagesRef.get();

    for (const messageDoc of messagesSnapshot.docs) {
      const data = messageDoc.data();
      const file = data.file;

      const isBase64Video = file && file.type && file.type.startsWith('video/') && file.url && file.url.startsWith('data:');
      const hasMediaData = data.mediaData && file && file.type && file.type.startsWith('video/');

      if (isBase64Video || hasMediaData) {
        console.log(`Found video to migrate in message ${messageDoc.id} from conversation ${convoDoc.id}`);

        const migrationPromise = (async () => {
          try {
            const base64Data = (file.url || data.mediaData).split(',')[1];

            // Upload to Cloudinary
            const uploadResponse = await new Promise((resolve, reject) => {
              const uploadStream = cloudinary.uploader.upload_stream(
                { resource_type: 'video', folder: 'migrated_videos' },
                (error, result) => {
                  if (error) return reject(error);
                  resolve(result);
                }
              );
              uploadStream.end(Buffer.from(base64Data, 'base64'));
            });

            if (!uploadResponse.secure_url) {
                throw new Error('Cloudinary did not return a secure_url');
            }

            // Update Firestore document
            await messageDoc.ref.update({
              'file.url': uploadResponse.secure_url,
              mediaData: admin.firestore.FieldValue.delete() // Remove the large base64 field
            });

            console.log(`Successfully migrated message ${messageDoc.id}. New URL: ${uploadResponse.secure_url}`);
            migratedCount++;
          } catch (error) {
            console.error(`Failed to migrate message ${messageDoc.id}:`, error);
          }
        })();
        migrationPromises.push(migrationPromise);
      }
    }
  }

  await Promise.all(migrationPromises);
  console.log(`\nMigration complete. Migrated ${migratedCount} video messages.`);
}

runMigration().catch(error => {
  console.error("Migration script failed with an error:", error);
  process.exit(1);
});

