                <MobileProvider>
                  <AppShell>
                    <main className="relative flex-1 z-10">{children}</main>
                  </AppShell>
                  <Toaster />
                </MobileProvider>
              </FriendsProvider>
            </AppearanceProvider>
          </ThemeProvider>
        </AuthProvider>
      </body>
    </html>
  );
}

