'use client';
import React, { useRef, useEffect } from 'react';
import './galaxy-background.css';
import { useIsMobile } from '@/hooks/use-mobile';

// Star properties type
type Star = {
  x: number;
  y: number;
  size: number;
  speed: number;
  opacity: number;
  opacityDirection: number;
};

// Component props
interface GalaxyBackgroundProps {
  starCount?: number;
  twinkle?: boolean;
}

export function GalaxyBackground({ starCount = 500, twinkle = true }: GalaxyBackgroundProps) {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationFrameId = useRef<number | null>(null);
  const isMobile = useIsMobile();
  const reducedMotion =
    typeof window !== 'undefined' && window.matchMedia('(prefers-reduced-motion: reduce)').matches;

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    let stars: Star[] = [];

    const setup = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      stars = [];
      const effectiveStarCount = reducedMotion ? starCount / 2 : starCount;
      for (let i = 0; i < effectiveStarCount; i++) {
        stars.push({
          x: Math.random() * canvas.width,
          y: Math.random() * canvas.height,
          size: Math.random() * 1.5 + 0.5,
          speed: Math.random() * 0.2 + 0.1,
          opacity: Math.random() * 0.5 + 0.2,
          opacityDirection: Math.random() > 0.5 ? 1 : -1,
        });
      }
    };

    const draw = () => {
      if (!ctx || !canvas) return;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // --- Enhanced Nebula Effect ---
      const nebulaOpacity1 = isMobile ? 0.6 : 0.4;
      const nebulaOpacity2 = isMobile ? 0.5 : 0.3;
      const radiusMultiplier1 = isMobile ? 0.8 : 0.5;
      const radiusMultiplier2 = isMobile ? 0.7 : 0.4;

      // Nebula 1
      const centerX1 = canvas.width * 0.25;
      const centerY1 = canvas.height * 0.25;
      const radius1 = Math.max(canvas.width, canvas.height) * radiusMultiplier1;
      const grad1 = ctx.createRadialGradient(centerX1, centerY1, 0, centerX1, centerY1, radius1);
      grad1.addColorStop(0, `hsla(260, 45%, 15%, ${nebulaOpacity1})`);
      grad1.addColorStop(1, 'transparent');
      ctx.fillStyle = grad1;
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Nebula 2
      const centerX2 = canvas.width * 0.75;
      const centerY2 = canvas.height * 0.75;
      const radius2 = Math.max(canvas.width, canvas.height) * radiusMultiplier2;
      const grad2 = ctx.createRadialGradient(centerX2, centerY2, 0, centerX2, centerY2, radius2);
      grad2.addColorStop(0, `hsla(220, 50%, 20%, ${nebulaOpacity2})`);
      grad2.addColorStop(1, 'transparent');
      ctx.fillStyle = grad2;
      ctx.fillRect(0, 0, canvas.width, canvas.height);


      for (const star of stars) {
        ctx.beginPath();
        ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
        ctx.fillStyle = `hsla(200, 80%, 90%, ${star.opacity})`;
        ctx.fill();
      }
    };

    const update = () => {
      if(!canvas) return;
      for (const star of stars) {
        // Star movement
        star.y -= star.speed;
        if (star.y < 0) {
          star.y = canvas.height;
          star.x = Math.random() * canvas.width;
        }

        // Twinkle effect
        if (twinkle && !reducedMotion) {
          star.opacity += 0.005 * star.opacityDirection;
          if (star.opacity > 0.8 || star.opacity < 0.1) {
            star.opacityDirection *= -1;
          }
        }
      }
    };

    const animate = () => {
      if (!reducedMotion) {
        update();
      }
      draw();
      animationFrameId.current = requestAnimationFrame(animate);
    };

    const handleResize = () => {
      setup();
      draw(); // Redraw immediately on resize
    };

    setup();
    draw(); // Draw the initial frame immediately
    animate();

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      if (animationFrameId.current) {
        cancelAnimationFrame(animationFrameId.current);
      }
    };
  }, [reducedMotion, starCount, twinkle, isMobile]);

  return <canvas ref={canvasRef} id="galaxy-canvas" aria-hidden="true"></canvas>;
}

src/components/gif-picker.tsx.content:
'use client';
import { useState, useEffect, useCallback, useMemo } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import { Input } from './ui/input';
import { ScrollArea } from './ui/scroll-area';
import { useToast } from '@/hooks/use-toast';
import { Loader2, Search } from 'lucide-react';
import Image from 'next/image';
import { Button } from './ui/button';
import { cn } from '@/lib/utils';
import { useDebounce } from '@/hooks/use-debounce';

const TENOR_API_KEY = process.env.NEXT_PUBLIC_TENOR_API_KEY;

interface Gif {
  id: string;
  url: string;
  preview: string;
  title: string;
}

interface GifPickerProps {
  children: React.ReactNode;
  onSelect: (base64: string, fileType: string, fileName: string, caption: string) => void;
}

const fileToBase64 = (file: Blob): Promise<string> => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result as string);
      reader.onerror = error => reject(error);
    });
};

async function fetchGifs(
  searchTerm: string,
  toast: (options: any) => void
): Promise<Gif[]> {
  if (!TENOR_API_KEY) {
      toast({
        title: 'Missing API Key',
        description: 'The Tenor API key is missing. Please add it to your environment variables.',
        variant: 'destructive',
      });
      return [];
  }

  const searchUrl = `https://tenor.googleapis.com/v2/search?q=${encodeURIComponent(
    searchTerm
  )}&key=${TENOR_API_KEY}&limit=20&media_filter=tinygif`;
  const trendingUrl = `https://tenor.googleapis.com/v2/featured?key=${TENOR_API_KEY}&limit=20&media_filter=tinygif`;

  const url = searchTerm ? searchUrl : trendingUrl;

  try {
    const response = await fetch(url);
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    return data.results.map((gif: any) => ({
      id: gif.id,
      url: gif.media_formats.gif?.url || gif.media_formats.tinygif.url,
      preview: gif.media_formats.tinygif.url,
      title: gif.content_description,
    })).filter((g: Gif) => g.url && g.preview);
  } catch (error) {
    console.error('Error fetching GIFs:', error);
    return [];
  }
}

export function GifPicker({ children, onSelect }: GifPickerProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const debouncedSearchTerm = useDebounce(searchTerm, 300);
  const [gifs, setGifs] = useState<Gif[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const { toast } = useToast();

  const handleSearch = useCallback(async (term: string) => {
    setIsLoading(true);
    try {
      const results = await fetchGifs(term, toast);
      setGifs(results);
    } catch (error) {
      console.error('Failed to fetch GIFs', error);
      toast({
        title: 'Error fetching GIFs',
        description: 'Could not load GIFs. Please try again later.',
        variant: 'destructive',
      });
    } finally {
      setIsLoading(false);
    }
  }, [toast]);

  useEffect(() => {
    if (isOpen) {
      handleSearch(debouncedSearchTerm);
    }
  }, [isOpen, debouncedSearchTerm, handleSearch]);

  const handleGifSelect = (gif: Gif) => {
    fetch(gif.url)
        .then(res => res.blob())
        .then(blob => fileToBase64(blob))
        .then(base64 => {
            onSelect(base64, 'image/gif', gif.title || 'vibez-gif.gif', '');
        })
        .catch(err => {
             console.error("Error fetching GIF blob", err);
             toast({
                title: "Couldn't send GIF",
                description: "There was a problem downloading the selected GIF.",
                variant: "destructive"
             })
        });

    setIsOpen(false);
  };

  const memoizedGifs = useMemo(() => {
    return gifs.map((gif) => (
        <button
            key={gif.id}
            onClick={() => handleGifSelect(gif)}
            className="rounded-md overflow-hidden aspect-video relative group focus:outline-none focus:ring-2 focus:ring-primary"
        >
            <Image
            src={gif.preview}
            alt={gif.title}
            fill
            className="object-cover group-hover:scale-110 transition-transform duration-200"
            unoptimized
            />
        </button>
    ))
  }, [gifs, handleGifSelect]);

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogTrigger asChild>{children}</DialogTrigger>
      <DialogContent className="sm:max-w-lg">
        <DialogHeader>
          <DialogTitle>Select a GIF</DialogTitle>
        </DialogHeader>
        <div className="relative my-4">
          <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
          <Input
            placeholder="Search for a GIF..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="pl-10"
          />
        </div>

        <ScrollArea className={cn("h-80 border rounded-md", isLoading && 'flex items-center justify-center')}>
            {isLoading ? (
                <div className="flex justify-center items-center h-full">
                    <Loader2 className="h-8 w-8 animate-spin text-primary" />
                </div>
            ) : gifs.length > 0 ? (
                <div className="p-2 grid grid-cols-2 gap-2">
                    {memoizedGifs}
                </div>
            ) : (
                <p className="text-center text-muted-foreground p-8">No GIFs found. Is your API key set?</p>
            )}
        </ScrollArea>
      </DialogContent>
    </Dialog>
  );
}

