'use client';
import React, { useState, useEffect, useRef } from 'react';
import {
  Sheet,
  SheetContent,
  SheetHeader,
  SheetTitle,
} from '@/components/ui/sheet';
import { Conversation, User } from '@/lib/types';
import { UserAvatar } from './user-avatar';
import { Button } from './ui/button';
import { ScrollArea } from './ui/scroll-area';
import { Badge } from './ui/badge';
import { Input } from './ui/input';
import { Textarea } from './ui/textarea';
import { doc, updateDoc } from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { useToast } from '@/hooks/use-toast';
import { Camera, Loader2 } from 'lucide-react';
import { ImagePreviewDialog } from './image-preview-dialog';

interface GroupProfileSheetProps {
  chat: Conversation;
  currentUser: User;
  isOpen: boolean;
  onOpenChange: (isOpen: boolean) => void;
  usersCache: Map<string, User>;
}

export function GroupProfileSheet({
  chat,
  currentUser,
  isOpen,
  onOpenChange,
  usersCache,
}: GroupProfileSheetProps) {
  const [name, setName] = useState(chat.name || '');
  const [description, setDescription] = useState(chat.description || '');
  const [isEditing, setIsEditing] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const { toast } = useToast();

  const fileInputRef = useRef<HTMLInputElement>(null);
  const [previewFile, setPreviewFile] = useState<File | null>(null);
  const [isUploading, setIsUploading] = useState(false);

  const [avatarUrl, setAvatarUrl] = useState(chat.avatar || '');
  const [newAvatarDataUrl, setNewAvatarDataUrl] = useState<string | null>(null);


  const allParticipants = chat.participants.map(p => usersCache.get(p)).filter(Boolean) as User[];
  const uniqueParticipantIds = new Set<string>();
  const participants = allParticipants.filter(p => {
    if (uniqueParticipantIds.has(p.uid)) {
      return false;
    }
    uniqueParticipantIds.add(p.uid);
    return true;
  });

  const adminId = chat.createdBy;
  const isAdmin = currentUser.uid === adminId;

  const resetLocalState = () => {
    setName(chat.name || '');
    setDescription(chat.description || '');
    setAvatarUrl(chat.avatar || '');
    setNewAvatarDataUrl(null);
    setIsEditing(false);
  };

  useEffect(() => {
    if (isOpen) {
      resetLocalState();
    }
  }, [chat, isOpen]);


  const handleAvatarUpload = async (file: File) => {
    setIsUploading(true);
    try {
        const dataUrl = await new Promise<string>((resolve, reject) => {
            const reader = new FileReader();
            reader.readAsDataURL(file);
            reader.onload = (event) => {
                if (!event.target?.result) {
                    return reject(new Error("Failed to read file."));
                }
                resolve(event.target.result as string);
            };
            reader.onerror = (error) => reject(new Error("Failed to read file for resizing."));
        });

        setNewAvatarDataUrl(dataUrl);
        setAvatarUrl(dataUrl); // also update for preview
        toast({ title: 'Success', description: 'Avatar ready to be saved.', variant: 'default' });

    } catch (error: any) {
        console.error("Error preparing avatar:", error);
        toast({ title: 'Error', description: error.message || 'Failed to prepare avatar. Please try again.', variant: 'destructive' });
    } finally {
        setIsUploading(false);
        setPreviewFile(null);
    }
  };

  const handleSaveChanges = async () => {
    if (!isAdmin) return;
    setIsSaving(true);
    try {
        const chatRef = doc(db, 'conversations', chat.id);
        const dataToUpdate: any = {};

        if (name !== (chat.name || '')) dataToUpdate.name = name;
        if (description !== (chat.description || '')) dataToUpdate.description = description;

        // Prioritize newly uploaded avatar
        if (newAvatarDataUrl) {
            dataToUpdate.avatar = newAvatarDataUrl;
        } else if (avatarUrl !== (chat.avatar || '')) {
            // Fallback to URL input if no new file was uploaded
            dataToUpdate.avatar = avatarUrl;
        }

        if (Object.keys(dataToUpdate).length > 0) {
            await updateDoc(chatRef, dataToUpdate);
        }

        toast({ title: "Success", description: "Group info updated successfully." });
        setIsEditing(false);
        setNewAvatarDataUrl(null); // Clear new avatar data after saving
    } catch (error) {
        console.error("Error updating group info:", error);
        toast({ title: "Error", description: "Failed to update group info.", variant: "destructive" });
    } finally {
        setIsSaving(false);
    }
  }

  const onSelectFile = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (e.target.files && e.target.files.length > 0) {
        const file = e.target.files[0];
        setPreviewFile(file);
        if(fileInputRef.current) fileInputRef.current.value = '';
    }
  };

  const isChanged = (chat.name || '') !== name || (chat.avatar || '') !== avatarUrl || (chat.description || '') !== description || newAvatarDataUrl;
  const currentAvatar = isEditing ? avatarUrl : chat.avatar;

  return (
    <>
    <Sheet open={isOpen} onOpenChange={(open) => {
        onOpenChange(open);
        if (!open) setIsEditing(false);
    }}>
      <SheetContent className="w-full max-w-md bg-background/90 backdrop-blur-lg p-0 flex flex-col">
        <SheetHeader className="p-6 pb-4 text-left">
          <SheetTitle>Group Info</SheetTitle>
        </SheetHeader>

        <div className="flex flex-col items-center justify-center p-6 pt-0 space-y-4 border-b">
            <div className="relative">
                <UserAvatar user={{ name: isEditing ? name : chat.name, photoURL: currentAvatar }} className="w-32 h-32 text-4xl" />
                <input
                    type="file"
                    ref={fileInputRef}
                    onChange={onSelectFile}
                    accept="image/*"
                    className="hidden"
                />
            </div>
            <div className="text-center">
              <h2 className="text-2xl font-bold">{isEditing ? name : chat.name}</h2>
              <p className="text-sm text-muted-foreground">{isEditing ? description : (chat.description || "No description.")}</p>
              <p className="text-xs text-muted-foreground pt-2">{participants.length} members</p>
            </div>
             {isAdmin && !isEditing && (
                <Button variant="outline" onClick={() => setIsEditing(true)}>Edit Info</Button>
            )}
        </div>

        <ScrollArea className="flex-1">
          <div className="p-6 space-y-4">
             {isAdmin && isEditing && (
                <div className="space-y-6">
                    <div className="space-y-3">
                        <Button asChild className="w-full">
                            <button onClick={() => fileInputRef.current?.click()} disabled={isUploading}>
                                {isUploading ? <Loader2 className="h-4 w-4 animate-spin mr-2" /> : <Camera className="h-4 w-4 mr-2"/>}
                                {isUploading ? 'Uploading...' : 'Upload from computer'}
                            </button>
                        </Button>
                    </div>
                    <div>
                        <label htmlFor="group-name" className="text-sm font-medium">Group Name</label>
                        <Input id="group-name" value={name} onChange={e => setName(e.target.value)} placeholder="Group name" disabled={isSaving}/>
                    </div>
                     <div>
                        <label htmlFor="group-description" className="text-sm font-medium">Description</label>
                        <Textarea id="group-description" value={description} onChange={e => setDescription(e.target.value)} placeholder="Group description..." disabled={isSaving}/>
                    </div>
                    <div className="flex justify-end gap-2">
                        <Button variant="ghost" onClick={() => setIsEditing(false)} disabled={isSaving}>Cancel</Button>
                        <Button onClick={handleSaveChanges} disabled={isSaving || !isChanged}>
                            {isSaving && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                            Save
                        </Button>
                    </div>
                </div>
             )}

              {!isEditing && (
                <>
                    <h3 className="font-semibold text-card-foreground">{participants.length} Members</h3>
                    <div className="space-y-3">
                        {participants.map(participant => (
                            <div key={participant.uid} className="flex items-center justify-between">
                            <div className="flex items-center gap-3">
                                <UserAvatar user={participant} className="h-10 w-10"/>
                                <div>
                                    <p className="font-semibold">{participant.name}</p>
                                    <p className="text-sm text-muted-foreground">{participant.email}</p>
                                </div>
                            </div>
                            {participant.uid === adminId && (
                                <Badge variant="outline">Admin</Badge>
                            )}
                            </div>
                        ))}
                    </div>
                </>
              )}
          </div>
        </ScrollArea>
      </SheetContent>
    </Sheet>
    {previewFile && (
        <ImagePreviewDialog
          file={previewFile}
          onSend={handleAvatarUpload}
          onCancel={() => setPreviewFile(null)}
          mode="avatar"
        />
    )}
    </>
  );
}

src/components/image-preview-dialog.tsx.content:
'use client';
import { useState, useEffect, useRef } from 'react';
import NextImage from 'next/image';
import { Dialog, DialogContent, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Loader2, File as FileIcon, Video } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import ReactCrop, { type Crop, centerCrop, makeAspectCrop } from 'react-image-crop';
import 'react-image-crop/dist/ReactCrop.css';

interface ImagePreviewDialogProps {
  file: File;
  onSend: (file: File, message: string) => Promise<any>;
  onCancel: () => void;
  mode: 'chat' | 'story' | 'avatar';
}

/**
 * Creates a centered, aspect-ratio-constrained crop rectangle.
 * @param mediaWidth The width of the media element.
 * @param mediaHeight The height of the media element.
 * @param aspect The desired aspect ratio.
 * @returns A Crop object.
 */
function centerAspectCrop(
  mediaWidth: number,
  mediaHeight: number,
  aspect: number,
): Crop {
  return centerCrop(
    makeAspectCrop(
      {
        unit: '%',
        width: 90,
      },
      aspect,
      mediaWidth,
      mediaHeight,
    ),
    mediaWidth,
    mediaHeight,
  );
}

/**
 * Generates a cropped, circular image from a source image and a crop object.
 * This function correctly handles image scaling and device pixel ratio for high-quality output.
 * @param image The source HTMLImageElement.
 * @param crop The crop parameters from ReactCrop (in percentages).
 * @param fileName The desired filename for the output file.
 * @returns A promise that resolves with the cropped image as a File object.
 */
async function getCroppedCircularImage(
  image: HTMLImageElement,
  crop: Crop,
  fileName: string
): Promise<File> {
  const canvas = document.createElement('canvas');
  const ctx = canvas.getContext('2d');

  if (!ctx) {
    throw new Error('Canvas context is not available');
  }

  // --- Avatar Cropping Logic ---
  // The following section implements the high-quality circular crop.

  const scaleX = image.naturalWidth / image.width;
  const scaleY = image.naturalHeight / image.height;
  const pixelRatio = window.devicePixelRatio || 1;

  // The crop object uses percentages, so we calculate the source crop region in pixels.
  const cropX = (crop.x / 100) * image.naturalWidth;
  const cropY = (crop.y / 100) * image.naturalHeight;
  const cropWidth = (crop.width / 100) * image.naturalWidth;
  const cropHeight = (crop.height / 100) * image.naturalHeight;

  // Set canvas dimensions to the actual crop size, scaled for high DPI.
  canvas.width = Math.floor(cropWidth * pixelRatio);
  canvas.height = Math.floor(cropHeight * pixelRatio);

  ctx.scale(pixelRatio, pixelRatio);
  ctx.imageSmoothingQuality = 'high';

  // Center the drawing within the canvas
  const centerX = cropWidth / 2;
  const centerY = cropHeight / 2;
  const radius = Math.min(centerX, centerY);

  // Create a circular clipping path. Anything drawn after this will be confined to the circle.
  ctx.beginPath();
  ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI, false);
  ctx.clip();

  // Draw the cropped portion of the source image onto the canvas.
  ctx.drawImage(
    image,
    cropX,
    cropY,
    cropWidth,
    cropHeight,
    0,
    0,
    cropWidth,
    cropHeight
  );

  // --- End of Avatar Cropping Logic ---

  return new Promise((resolve, reject) => {
    canvas.toBlob((blob) => {
      if (!blob) {
        reject(new Error('Canvas is empty'));
        return;
      }
      // Resolve with a new File object. Using PNG to preserve transparency outside the circle.
      resolve(new File([blob], fileName, { type: 'image/png' }));
    }, 'image/png', 1); // Use PNG for transparency and high quality.
  });
}


export function ImagePreviewDialog({ file, onSend, onCancel, mode }: ImagePreviewDialogProps) {
  const [message, setMessage] = useState('');
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [isSending, setIsSending] = useState(false);
  const { toast } = useToast();

  const [crop, setCrop] = useState<Crop>();
  const imgRef = useRef<HTMLImageElement | null>(null);

  useEffect(() => {
    if (!file || !file.type) {
      onCancel();
      return;
    }
    const url = URL.createObjectURL(file);
    setPreviewUrl(url);

    return () => {
      URL.revokeObjectURL(url);
    };
  }, [file, onCancel]);

  /**
   * When an image loads, automatically set a centered square crop if in 'avatar' mode.
   */
  const onImageLoad = (e: React.SyntheticEvent<HTMLImageElement>) => {
    imgRef.current = e.currentTarget;
    if (mode === 'avatar') {
      const { width, height } = e.currentTarget;
      // Set a centered, 1:1 aspect ratio crop.
      setCrop(centerAspectCrop(width, height, 1 / 1));
    }
  };

  const handleSend = async () => {
    setIsSending(true);

    let fileToSend = file;

    try {
      // --- File Generation Logic ---
      // If in avatar mode, generate the cropped file before sending.
      if (mode === 'avatar' && imgRef.current && crop?.width && crop?.height) {
        fileToSend = await getCroppedCircularImage(imgRef.current, crop, 'avatar.png');
      }
      // For 'chat' and 'story' modes, fileToSend remains the original file.

      await onSend(fileToSend, message);

    } catch (error) {
      console.error('Image upload/crop failed:', error);
      toast({
        title: "Action Failed",
        description: "Could not process the image. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsSending(false);
      onCancel();
    }
  };

  if (!file || !file.type) return null;

  const isImage = file.type.startsWith('image/');
  const isVideo = file.type.startsWith('video/');

  const getTitle = () => {
    switch (mode) {
      case 'story': return "Post a Story";
      case 'avatar': return "Set New Avatar";
      default: return "Send File";
    }
  }

  const getButtonText = () => {
    switch (mode) {
      case 'story': return "Post Story";
      case 'avatar': return "Set as Avatar";
      default: return "Send";
    }
  }

  return (
    <Dialog open={true} onOpenChange={(isOpen) => !isOpen && onCancel()}>
      <DialogContent className="max-w-lg w-full">
        <DialogHeader>
          <DialogTitle>{getTitle()}</DialogTitle>
        </DialogHeader>

        <div className="flex items-center justify-center p-4 bg-muted/30 rounded-lg max-h-[50vh] min-h-[200px]">
          {isImage && previewUrl && (
            mode === 'avatar' ? (
              <ReactCrop
                crop={crop}
                onChange={c => setCrop(c)}
                circularCrop
                aspect={1}
              >
                <img
                  ref={imgRef}
                  src={previewUrl}
                  alt="Image preview"
                  style={{ maxHeight: '50vh', display: 'block' }}
                  onLoad={onImageLoad}
                />
              </ReactCrop>
            ) : (
              <NextImage
                src={previewUrl}
                alt="Image preview"
                width={500}
                height={500}
                style={{ objectFit: 'contain', maxHeight: '50vh', width: 'auto', height: 'auto' }}
              />
            )
          )}
          {isVideo && previewUrl && (
              <video src={previewUrl} controls className="max-h-[50vh] rounded-lg" />
          )}
          {!isImage && !isVideo && (
            <div className="flex flex-col items-center gap-4 text-muted-foreground">
              <FileIcon className="w-16 h-16"/>
              <p className="font-semibold">{file.name}</p>
              <p className="text-sm">{(file.size / 1024 / 1024).toFixed(2)} MB</p>
            </div>
          )}
        </div>

        {mode !== 'avatar' && (
          <div className="relative">
            <Textarea
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              placeholder={mode === 'story' ? 'Add a caption...' : 'Add a message...'}
              className="pr-20"
              rows={1}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  handleSend();
                }
              }}
            />
          </div>
        )}

        <DialogFooter className="mt-2">
          <Button variant="outline" onClick={onCancel} disabled={isSending}>
            Cancel
          </Button>
          <Button onClick={handleSend} disabled={isSending}>
            {isSending && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
            {isSending ? 'Sending...' : getButtonText()}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

src/components/message-bubble.tsx.content:
'use client';

import { Message, User } from '@/lib/types';
import { cn } from '@/lib/utils';
import { UserAvatar } from './user-avatar';
import { Check, CheckCheck, Clock, File as FileIcon, Download, Image as ImageIcon, Smile, MoreHorizontal, Reply, Trash2, Video, Volume2 } from 'lucide-react';
import { Timestamp } from 'firebase/firestore';
import Image from 'next/image';
import { motion, PanInfo } from 'framer-motion';
import { UploadProgress } from './upload-progress';
import { Popover, PopoverContent, PopoverTrigger } from './ui/popover';
import { Button } from './ui/button';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuTrigger } from './ui/dropdown-menu';
import { memo } from 'react';
import React from 'react';

interface MessageBubbleProps {
  message: Message;
  sender?: User;
  isCurrentUser: boolean;
  progress?: number;
  onCancelUpload: () => void;
  onMessageAction: (messageId: string, action: 'react' | 'delete', data?: any) => void;
  onReply: (message: Message) => void;
  isRead: boolean;
}

const isImage = (fileType: string) => fileType.startsWith('image/');
const isAudio = (fileType: string) => fileType.startsWith('audio/');
const isVideo = (fileType: string) => fileType.startsWith('video/');

const defaultEmojis = ['ðŸ‘', 'â¤ï¸', 'ðŸ˜‚', 'ðŸ˜®', 'ðŸ˜¢', 'ðŸ™'];

const messageVariants = {
    initial: { opacity: 0, y: 10, scale: 0.95 },
    animate: { opacity: 1, y: 0, scale: 1, transition: { duration: 0.2, ease: [0.25, 1, 0.5, 1] } },
};

function MessageBubble({ message, sender, isCurrentUser, progress, onCancelUpload, onMessageAction, onReply, isRead }: MessageBubbleProps) {
  if (!sender) {
    return (
        <div className={cn('group flex w-full items-start gap-3', isCurrentUser && 'flex-row-reverse')}>
            <div className="h-8 w-8 rounded-full bg-muted animate-pulse" />
            <div className={cn('relative flex max-w-[70%] flex-col rounded-xl px-4 py-2', isCurrentUser ? 'rounded-tr-none bg-primary text-primary-foreground' : 'rounded-tl-none bg-card')}>
                <p className="text-base">{message.text}</p>
            </div>
        </div>
    );
  }

  const getReadReceiptIcon = () => {
    if (message.status === 'sending') return <Clock className="h-4 w-4 text-primary-foreground/70" />;
    if (isRead) return <CheckCheck className="h-4 w-4 text-blue-400" />;
    if (message.status === 'sent' || message.status === 'delivered' || message.status === 'read') return <CheckCheck className="h-4 w-4 text-primary-foreground/70" />;
    return <Check className="h-4 w-4 text-primary-foreground/70" />;
  }


  const getFormattedTimestamp = (timestamp: any) => {
    if (!timestamp) return '';

    let date: Date;
    if (timestamp instanceof Timestamp) {
        date = timestamp.toDate();
    } else if (timestamp instanceof Date) {
        date = timestamp;
    } else if (typeof timestamp === 'string') {
        date = new Date(timestamp);
    } else if (timestamp?.seconds) {
        date = new Date(timestamp.seconds * 1000);
    } else {
        return "Sending..."
    }

    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const formattedTimestamp = getFormattedTimestamp(message.timestamp);

  const renderMessageContent = () => {
     if (message.deleted) {
        return <p className="text-base italic text-muted-foreground">This message was deleted.</p>;
     }
    if (message.file) {
      const isSending = message.status === 'sending';
      const fileContent = (() => {
        if (isImage(message.file.type)) {
            const imageUrl = message.file.url;
            const isGif = message.file.type === 'image/gif';
            if (!imageUrl) {
                return (
                    <div className="w-[250px] h-[250px] bg-muted rounded-lg flex items-center justify-center">
                        <ImageIcon className="w-10 h-10 text-muted-foreground" />
                    </div>
                );
            }
            return (
                <a href={imageUrl} target="_blank" rel="noopener noreferrer">
                    <Image
                        key={message.id}
                        src={imageUrl}
                        alt={message.file.name}
                        width={250}
                        height={250}
                        className={cn(
                        "rounded-lg object-cover max-w-full",
                        isSending && 'opacity-60'
                        )}
                        unoptimized={isGif}
                    />
                </a>
            )
        }
        if (isAudio(message.file.type)) {
            return (
                message.file.url ? <audio controls src={message.file.url} className={cn("w-full max-w-xs", isSending && 'opacity-60')} /> : null
            )
        }
        if (isVideo(message.file.type)) {
            return (
                message.file.url ? <video controls src={message.file.url} className={cn("w-full max-w-xs rounded-lg", isSending && 'opacity-60')} /> : null
            )
        }
        return (
            <a 
            href={message.file.url} 
            target="_blank" 
            rel="noopener noreferrer" 
            className="flex items-center gap-3 bg-muted/50 p-3 rounded-lg hover:bg-muted"
            >
            <FileIcon className="h-8 w-8 text-muted-foreground" />
            <div className="flex-1 overflow-hidden">
                <p className="font-medium truncate">{message.file.name}</p>
                <p className="text-sm text-muted-foreground">Click to download</p>
            </div>
            <Download className="h-5 w-5 text-muted-foreground" />
            </a>
        )
      })();

      return (
        <div className="relative">
          {fileContent}
          {isSending && (
            <UploadProgress progress={progress} onCancel={onCancelUpload} />
          )}
        </div>
      );
    }
    return null;
  }

  const hasContent = message.text || message.file;

  const handleDragEnd = (event: MouseEvent | TouchEvent | PointerEvent, info: PanInfo) => {
    const swipeThreshold = 50;
    if (info.offset.x > swipeThreshold && !isCurrentUser) {
      onReply(message);
    }
    if (info.offset.x < -swipeThreshold && isCurrentUser) {
      onReply(message);
    }
  };

  const MessageActions = () => (
    <div className={cn(
        "absolute top-0 -translate-y-1/2 flex items-center bg-background border rounded-full p-0.5 shadow-md transition-opacity duration-200 opacity-0 group-hover:opacity-100",
        isCurrentUser ? 'right-2' : 'left-2'
    )}>
        <Popover>
            <PopoverTrigger asChild>
                <Button variant="ghost" size="icon" className="h-7 w-7 rounded-full">
                    <Smile className="h-4 w-4" />
                </Button>
            </PopoverTrigger>
            <PopoverContent className="p-1 w-auto">
                <div className="flex gap-1">
                    {defaultEmojis.map(emoji => (
                        <Button
                            key={emoji}
                            variant="ghost"
                            size="icon"
                            className="h-8 w-8 text-xl rounded-full"
                            onClick={() => onMessageAction(message.id, 'react', emoji)}
                        >
                            {emoji}
                        </Button>
                    ))}
                </div>
            </PopoverContent>
        </Popover>
        <DropdownMenu>
            <DropdownMenuTrigger asChild>
                <Button variant="ghost" size="icon" className="h-7 w-7 rounded-full">
                    <MoreHorizontal className="h-4 w-4" />
                </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent>
                <DropdownMenuItem onClick={() => onReply(message)}>
                    <Reply className="mr-2 h-4 w-4" />
                    <span>Reply</span>
                </DropdownMenuItem>
                {isCurrentUser && (
                  <DropdownMenuItem
                    className="text-destructive"
                    onClick={() => onMessageAction(message.id, 'delete')}
                  >
                    <Trash2 className="mr-2 h-4 w-4" />
                    <span>Delete</span>
                  </DropdownMenuItem>
                )}
            </DropdownMenuContent>
        </DropdownMenu>
    </div>
  );

  return (
    <motion.div
      variants={messageVariants}
      initial="initial"
      animate="animate"
      layout
      className={cn(
        'group flex w-full items-start gap-3 relative',
        isCurrentUser && 'flex-row-reverse'
      )}
      // Swipe to reply gesture
      drag="x"
      dragConstraints={{ left: 0, right: 0 }}
      onDragEnd={handleDragEnd}
      dragElastic={{ right: isCurrentUser ? 0 : 0.1, left: isCurrentUser ? 0.1 : 0 }}
      style={{ x: 0 }} // Initialize x to avoid layout jumps
    >
      <UserAvatar user={sender} className="h-8 w-8" />
      <div
        className={cn(
          'relative flex max-w-[70%] flex-col rounded-xl shadow-md',
          isCurrentUser
            ? 'rounded-tr-none bg-gradient-to-br from-gradient-from to-gradient-to text-primary-foreground animated-gradient'
            : 'rounded-tl-none bg-card',
            (message.file && !message.text) ? 'p-1.5' : 'px-4 py-2'
        )}
      >
        {!isCurrentUser && (
            <p className="text-sm font-semibold text-primary px-2 pt-1 font-heading">{sender.name}</p>
        )}

        {message.replyTo && (
          <div className={cn(
            "p-2 mb-2 bg-black/20 rounded-md",
            message.replyTo.storyMedia && "flex items-center gap-2"
          )}>
                {message.replyTo.storyMedia && (
                    <Image src={message.replyTo.storyMedia} alt="Story reply" width={40} height={40} className="rounded-md object-cover h-10 w-10" />
                )}
                <div>
                  <p className="text-sm font-semibold">{message.replyTo.messageSender}</p>
                  <p className="text-sm text-white/80 line-clamp-2">{message.replyTo.messageText}</p>
                </div>
          </div>
        )}

        {renderMessageContent()}

    {message.text && (
      <p className={cn(
        "text-base whitespace-pre-wrap break-words chat-list-force-break",
        (message.file) ? "mt-2 px-2 pb-1" : "",
        message.deleted && "italic text-muted-foreground"
      )}>
        {message.text}
      </p>
    )}

        {message.reactions && message.reactions.length > 0 && (
            <div className={cn(
                "absolute -bottom-4 flex gap-1",
                isCurrentUser ? 'left-2' : 'right-2'
            )}>
                {message.reactions.map(r => (
                    <div key={r.emoji} className="flex items-center bg-background/70 backdrop-blur-md border rounded-full px-2 py-0.5 text-xs shadow">
                        <span>{r.emoji}</span>
                        <span className="ml-1 font-semibold">{r.count}</span>
                    </div>
                ))}
            </div>
        )}

        {hasContent && !message.deleted && <MessageActions />}

        <div className="mt-1 flex items-center gap-2 self-end px-2">
          <p className={cn('text-xs', isCurrentUser ? 'text-primary-foreground/70' : 'text-muted-foreground')}>
            {formattedTimestamp}
          </p>
          {isCurrentUser && getReadReceiptIcon()}
        </div>
      </div>
    </motion.div>
  );
}

const MemoizedMessageBubble = memo(MessageBubble);
export { MemoizedMessageBubble as MessageBubble };

src/components/message-input.tsx.content:
'use client';

import { Paperclip, SendHorizonal, Mic, Trash2, StopCircle, Play, Smile } from 'lucide-react';
import { Button } from './ui/button';
import { Textarea } from './ui/textarea';
import { useState, useRef, ChangeEvent, useEffect } from 'react';
import { cn } from '@/lib/utils';
import { useToast } from '@/hooks/use-toast';
import { GifPicker } from './gif-picker';

interface MessageInputProps {
  onSendMessage: (messageText: string) => void;
  onFileSelect: (file: File) => void;
  onGifSelect: (base64: string, fileType: string, fileName: string, caption: string) => void;
  onTyping: (isTyping: boolean) => void;
  isAiChat?: boolean;
}

// #region WAV Encoder & Base64 Converter
// This set of functions provides a pure, client-side way to encode raw audio data (PCM)
// into a standard, universally playable WAV file format. It avoids server-side dependencies
// and ensures cross-browser compatibility for recorded audio.
const encodeWAV = (samples: Float32Array, sampleRate: number): Blob => {
    const buffer = new ArrayBuffer(44 + samples.length * 2);
    const view = new DataView(buffer);
    const writeString = (view: DataView, offset: number, string: string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };
    const floatTo16BitPCM = (output: DataView, offset: number, input: Float32Array) => {
      for (let i = 0; i < input.length; i++, offset += 2) {
        const s = Math.max(-1, Math.min(1, input[i]));
        output.setInt16(offset, s < 0 ? s * 0x8000 : s * 0x7FFF, true);
      }
    };
    writeString(view, 0, 'RIFF');
    view.setUint32(4, 36 + samples.length * 2, true);
    writeString(view, 8, 'WAVE');
    writeString(view, 12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, 1, true);
    view.setUint32(24, sampleRate, true);
    view.setUint32(28, sampleRate * 2, true);
    view.setUint16(32, 2, true);
    view.setUint16(34, 16, true);
    writeString(view, 36, 'data');
    view.setUint32(40, samples.length * 2, true);
    floatTo16BitPCM(view, 44, samples);
    return new Blob([view], { type: 'audio/wav' });
};
const blobToArrayBuffer = (blob: Blob): Promise<ArrayBuffer> => {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result as ArrayBuffer);
        reader.onerror = reject;
        reader.readAsArrayBuffer(blob);
    });
};
// #endregion

export function MessageInput({ onSendMessage, onFileSelect, onGifSelect, onTyping, isAiChat }: MessageInputProps) {
    const [message, setMessage] = useState('');
    const fileInputRef = useRef<HTMLInputElement>(null);
    const textareaRef = useRef<HTMLTextAreaElement>(null);
    const { toast } = useToast();

    const [recordingStatus, setRecordingStatus] = useState<'idle' | 'recording' | 'recorded'>('idle');
    const [recordedAudio, setRecordedAudio] = useState<{ blob: Blob, url: string } | null>(null);
    const mediaRecorderRef = useRef<MediaRecorder | null>(null);
    const audioChunksRef = useRef<Blob[]>([]);

    const [isSending, setIsSending] = useState(false);
    const [recordingDuration, setRecordingDuration] = useState(0);
    const timerIntervalRef = useRef<NodeJS.Timeout | null>(null);

    const reviewAudioRef = useRef<HTMLAudioElement>(null);
    const typingTimeoutRef = useRef<NodeJS.Timeout | null>(null);

    const handleTypingChange = (e: ChangeEvent<HTMLTextAreaElement>) => {
        setMessage(e.target.value);
        if (isAiChat) return;

        onTyping(true);
        if (typingTimeoutRef.current) {
            clearTimeout(typingTimeoutRef.current);
        }
        typingTimeoutRef.current = setTimeout(() => {
            onTyping(false);
        }, 1000);
    }

    const handleSend = () => {
        if(!message.trim() || isSending) return;

        setIsSending(true);
        onSendMessage(message.trim());
        setMessage('');
        if(typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
        onTyping(false);

        // Explicitly focus the textarea after sending to keep keyboard open
        textareaRef.current?.focus();

        // Cooldown to prevent ghost clicks on mobile
        setTimeout(() => {
            setIsSending(false);
        }, 50);
    }

    const handleFileChange = async (event: ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0];
        if (file) {
            const maxSize = 50 * 1024 * 1024; // 50MB
            if (file.size > maxSize) {
                toast({
                    title: 'File Too Large',
                    description: `Please select a file smaller than ${maxSize / 1024 / 1024}MB.`,
                    variant: 'destructive',
                });
                return;
            }
            onFileSelect(file);
        }
        if(fileInputRef.current) {
            fileInputRef.current.value = '';
        }
    }

    const startRecording = async () => {
        if (isSending) return;
        try {
            if (!navigator.mediaDevices?.getUserMedia) {
              toast({ title: "Recording not supported", description: "Your browser does not support audio recording.", variant: "destructive" });
              return;
            }
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            // Using a specific MIME type is not reliable, so we record with the default
            // and convert to a universally supported format (WAV) later.
            mediaRecorderRef.current = new MediaRecorder(stream);

            mediaRecorderRef.current.addEventListener("dataavailable", (event) => {
              if (event.data.size > 0) {
                audioChunksRef.current.push(event.data);
              }
            });

            mediaRecorderRef.current.start();
            setRecordingStatus('recording');
            setRecordingDuration(0);
            timerIntervalRef.current = setInterval(() => {
                setRecordingDuration(prev => prev + 1);
            }, 1000);
        } catch (err) {
            console.error("Error accessing microphone:", err);
            toast({ title: 'Microphone Access Denied', description: 'Please enable microphone permissions in your browser settings.', variant: 'destructive'});
        }
    };

    const stopRecording = (): Promise<Blob> => {
      return new Promise((resolve) => {
          if (timerIntervalRef.current) clearInterval(timerIntervalRef.current);
          if (!mediaRecorderRef.current || mediaRecorderRef.current.state === 'inactive') {
              resolve(new Blob());
              return;
          }

          mediaRecorderRef.current.addEventListener('stop', () => {
              const audioBlob = new Blob(audioChunksRef.current, { type: audioChunksRef.current[0]?.type || 'audio/webm' });
              audioChunksRef.current = [];
              mediaRecorderRef.current?.stream.getTracks().forEach(track => track.stop());
              mediaRecorderRef.current = null;
              resolve(audioBlob);
          }, { once: true });

          mediaRecorderRef.current.stop();
      });
    };


    const handleStopRecording = async () => {
        const recordedBlob = await stopRecording();

        if (recordedBlob.size === 0) {
            console.error("Recording failed, blob is empty.");
            cancelRecording();
            return;
        }

        try {
            // Convert the recorded audio (likely webm/opus) to a WAV file for universal compatibility.
            const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
            const arrayBuffer = await blobToArrayBuffer(recordedBlob);
            const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
            const pcmData = audioBuffer.getChannelData(0);
            const wavBlob = encodeWAV(pcmData, audioBuffer.sampleRate);

            const audioUrl = URL.createObjectURL(wavBlob);
            setRecordedAudio({ blob: wavBlob, url: audioUrl });
            setRecordingStatus('recorded');

        } catch (error) {
            console.error("Failed to convert audio to WAV:", error);
            toast({ title: 'Conversion Failed', description: 'Could not process recorded audio.', variant: 'destructive' });
            cancelRecording();
        }
    };

    const cancelRecording = () => {
        if (mediaRecorderRef.current && mediaRecorderRef.current.state === 'recording') {
             mediaRecorderRef.current.stream.getTracks().forEach(track => track.stop());
        }
        if (timerIntervalRef.current) clearInterval(timerIntervalRef.current);
        if (recordedAudio) URL.revokeObjectURL(recordedAudio.url);

        setRecordedAudio(null);
        mediaRecorderRef.current = null;
        audioChunksRef.current = [];
        setRecordingStatus('idle');
        setRecordingDuration(0);
    }

    const handleSendRecording = () => {
      if (recordedAudio) {
        const audioFile = new File([recordedAudio.blob], `voice-note-${Date.now()}.wav`, { type: 'audio/wav' });
        onFileSelect(audioFile);
        cancelRecording();
      }
    }

    useEffect(() => {
        return () => {
            if (timerIntervalRef.current) clearInterval(timerIntervalRef.current);
            if (recordedAudio) URL.revokeObjectURL(recordedAudio.url);
            if(typingTimeoutRef.current) clearTimeout(typingTimeoutRef.current);
            if (!isAiChat) {
                onTyping(false);
            }
        }
    }, [recordedAudio, isAiChat, onTyping]);

    const formatDuration = (seconds: number) => {
        const minutes = Math.floor(seconds / 60);
        const remainingSeconds = seconds % 60;
        return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
    }

  return (
    <div className="border-t border-border/50 bg-card/80 backdrop-blur-sm p-2">
      <div className="flex items-end gap-2">
        {!isAiChat && recordingStatus === 'idle' && (
            <>
                <input
                    type="file"
                    ref={fileInputRef}
                    onChange={handleFileChange}
                    className="hidden"
                    accept="image/*,video/*,audio/*"
                />
                <Button variant="ghost" size="icon" className="shrink-0" onClick={() => fileInputRef.current?.click()}>
                    <Paperclip className="h-5 w-5" />
                    <span className="sr-only">Attach file</span>
                </Button>
                <GifPicker onSelect={onGifSelect}>
                    <Button variant="ghost" size="icon" className="shrink-0">
                        <Smile className="h-5 w-5" />
                        <span className="sr-only">Select a GIF</span>
                    </Button>
                </GifPicker>
            </>
        )}

        <div className="flex-1 relative">
            {recordingStatus === 'recording' ? (
                <div className="flex-1 flex items-center justify-between bg-background/50 rounded-2xl px-4 py-2 h-10">
                    <div className="flex items-center gap-2 text-red-500">
                        <Mic className="h-5 w-5 animate-pulse" />
                        <span>{formatDuration(recordingDuration)}</span>
                    </div>
                    <Button variant="destructive" size="icon" className="rounded-full shrink-0 h-8 w-8" onClick={handleStopRecording}>
                        <StopCircle className="h-5 w-5" />
                        <span className="sr-only">Stop recording</span>
                    </Button>
                </div>
            ) : recordingStatus === 'recorded' && recordedAudio ? (
              <div className="flex-1 flex items-center justify-between bg-background/50 rounded-2xl px-4 py-2 h-10">
                  <div className="flex items-center gap-2">
                    <Button variant="ghost" size="icon" className="shrink-0 text-red-500 hover:text-red-600 h-8 w-8" onClick={cancelRecording}>
                        <Trash2 className="h-5 w-5"/>
                    </Button>
                    <audio ref={reviewAudioRef} src={recordedAudio.url} className="hidden"/>
                    <Button variant="outline" size="icon" className="h-8 w-8" onClick={() => reviewAudioRef.current?.play()}>
                        <Play className="h-4 w-4" />
                    </Button>
                     <span className="text-sm">{formatDuration(recordingDuration)}</span>
                  </div>

                  <div className="flex items-center gap-2">
                      <Button size="icon" className="rounded-full shrink-0 h-8 w-8" onClick={handleSendRecording}>
                          <SendHorizonal className="h-4 w-4" />
                          <span className="sr-only">Send voice message</span>
                      </Button>
                  </div>
              </div>
            ) : (
                <Textarea
                    ref={textareaRef}
                    value={message}
                    onChange={handleTypingChange}
                    placeholder="Type a message..."
                    className="flex-1 resize-none self-center rounded-2xl border-input bg-background/50 py-2.5 px-4 pr-12 min-h-0 h-10 max-h-24"
                    rows={1}
                    onKeyDown={(e) => {
                        if(e.key === 'Enter' && !e.shiftKey) {
                            e.preventDefault();
                            handleSend();
                        }
                    }}
                />
            )}
        </div>

        {message.trim() && recordingStatus === 'idle' ? (
            <Button
              type="button"
              size="icon"
              className="rounded-full shrink-0"
              onClick={handleSend}
            >
                <SendHorizonal className="h-5 w-5" />
                <span className="sr-only">Send message</span>
            </Button>
        ) : recordingStatus === 'idle' && !isAiChat ? (
            <Button size="icon" className="rounded-full shrink-0" onClick={startRecording} disabled={isSending}>
                <Mic className="h-5 w-5" />
                <span className="sr-only">Record voice message</span>
            </Button>
        ) : null}
      </div>
    </div>
  );
}

src/components/message-list.tsx.content:
'use client';
import { useEffect, useRef, memo, Fragment, forwardRef, UIEvent, useState, useLayoutEffect } from 'react';
import { motion } from 'framer-motion';
import { ScrollArea } from './ui/scroll-area';
import { Timestamp } from 'firebase/firestore';
import { format, isToday, isYesterday, isSameDay } from 'date-fns';
import { Loader2 } from 'lucide-react';
import { Button } from './ui/button';
import { MessageBubble } from './message-bubble';
import { User, Message } from '@/lib/types';
import { UserAvatar } from './user-avatar';


const AI_USER_ID = 'gemini-ai-chat-bot-7a4b9c1d-f2e3-4d56-a1b2-c3d4e5f6a7b8';

const messageListVariants = {
    initial: {},
    animate: {
        transition: {
            staggerChildren: 0.08,
        }
    }
};

const MemoizedMessageBubble = memo(MessageBubble);

interface MessageListProps {
    messages: Message[];
    currentUser: User;
    usersCache: Map<string, User>;
    uploadProgress: Map<string, number>;
    onCancelUpload: (messageId: string) => void;
    onMessageAction: (messageId: string, action: 'react' | 'delete', data?: any) => void;
    onReply: (message: Message) => void;
    isAiReplying: boolean;
    otherParticipantLastRead?: Timestamp;
    onLoadMore: () => Promise<void>;
    hasMore: boolean;
    isLoadingMore: boolean;
    chatId: string;
}

function convertToDate(timestamp: any): Date | null {
    if (!timestamp) return null;
    if (timestamp instanceof Timestamp) return timestamp.toDate();
    if (timestamp instanceof Date) return timestamp;
    if (typeof timestamp === 'string') return new Date(timestamp);
    if (timestamp?.seconds) return new Date(timestamp.seconds * 1000);
    return null;
}

function formatDateSeparator(date: Date): string {
    if (isToday(date)) return 'Today';
    if (isYesterday(date)) return 'Yesterday';
    return format(date, 'MMMM d, yyyy');
}


export const MessageList = forwardRef<HTMLDivElement, MessageListProps>(({
    messages,
    currentUser,
    usersCache,
    uploadProgress,
    onCancelUpload,
    onMessageAction,
    onReply,
    isAiReplying,
    otherParticipantLastRead,
    onLoadMore,
    hasMore,
    isLoadingMore,
    chatId,
}, ref) => {

    const viewportRef = useRef<HTMLDivElement>(null);
    const [isManualLoading, setIsManualLoading] = useState(false);
    const prevChatId = useRef(chatId);
    const [scrollAnchor, setScrollAnchor] = useState<{ top: number; height: number } | null>(null);
    const [isInitialLoad, setIsInitialLoad] = useState(true);

    const visibleMessages = messages.filter(message => {
        const blockedUsers = currentUser?.blockedUsers || [];
        return !blockedUsers.includes(message.senderId);
    });

    useEffect(() => {
        if (chatId !== prevChatId.current) {
            setIsInitialLoad(true);
            prevChatId.current = chatId;
        }
    }, [chatId]);


    useLayoutEffect(() => {
        const viewport = viewportRef.current;
        if (isInitialLoad && viewport && messages.length > 0) {
            viewport.scrollTop = viewport.scrollHeight;
            setIsInitialLoad(false);
        }

        if (scrollAnchor && viewport) {
            viewport.scrollTop = scrollAnchor.top + (viewport.scrollHeight - scrollAnchor.height);
            setScrollAnchor(null);
        }
    }, [messages, isInitialLoad, scrollAnchor]);


    const handleLoadMore = async () => {
        if (!viewportRef.current || isLoadingMore) return;

        setScrollAnchor({
            top: viewportRef.current.scrollTop,
            height: viewportRef.current.scrollHeight,
        });

        setIsManualLoading(true);

        try {
            await onLoadMore();
        } finally {
            setIsManualLoading(false);
        }
    };

    const getParticipantDetails = (senderId: string) => {
        if (senderId === currentUser.uid) return currentUser;
        return usersCache.get(senderId);
    }

    return (
        <ScrollArea className="h-full" viewportRef={ref as React.RefObject<HTMLDivElement>}>
            <motion.div 
                ref={viewportRef}
                className="p-4 space-y-6"
                variants={messageListVariants}
                initial="initial"
                animate="animate"
            >
                {hasMore && (
                    <div className="flex justify-center">
                        <Button
                            variant="secondary"
                            onClick={handleLoadMore}
                            disabled={isManualLoading || isLoadingMore}
                        >
                            {(isManualLoading || isLoadingMore) ? (
                                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                            ) : null}
                            Load Older Messages
                        </Button>
                    </div>
                )}
                {messages.length > 0 ? (
                    visibleMessages.map((message, index) => {
                        const sender = getParticipantDetails(message.senderId);

                        let isRead = false;
                        if(otherParticipantLastRead && message.timestamp && message.senderId === currentUser.uid) {
                            const messageDate = convertToDate(message.timestamp);
                            const lastReadDate = convertToDate(otherParticipantLastRead);
                            if (messageDate && lastReadDate) {
                                isRead = messageDate <= lastReadDate;
                            }
                        }

                        let dateSeparator = null;
                        const currentMessageDate = convertToDate(message.timestamp);
                        if (currentMessageDate) {
                            if (index === 0) {
                                dateSeparator = formatDateSeparator(currentMessageDate);
                            } else {
                                const prevMessageDate = convertToDate(visibleMessages[index - 1].timestamp);
                                if (prevMessageDate && !isSameDay(currentMessageDate, prevMessageDate)) {
                                    dateSeparator = formatDateSeparator(currentMessageDate);
                                }
                            }
                        }

                        return (
                            <Fragment key={message.id || message.clientTempId}>
                                {dateSeparator && (
                                    <div className="relative text-center my-4">
                                        <hr className="absolute top-1/2 left-0 w-full -translate-y-1/2" />
                                        <span className="relative bg-background px-2 text-xs text-muted-foreground">{dateSeparator}</span>
                                    </div>
                                )}
                                <MemoizedMessageBubble
                                    message={message}
                                    sender={sender}
                                    isCurrentUser={sender?.uid === currentUser.uid}
                                    progress={uploadProgress.get(message.clientTempId || message.id)}
                                    onCancelUpload={() => onCancelUpload(message.clientTempId || message.id)}
                                    onMessageAction={onMessageAction}
                                    onReply={onReply}
                                    isRead={isRead}
                                />
                            </Fragment>
                        );
                })
                ) : (
                <div className="flex justify-center items-center h-full">
                    <div className="text-center p-4 rounded-lg bg-background/50">
                    <p className="text-muted-foreground">
                        No messages in this chat yet.
                    </p>
                    </div>
                </div>
                )}
                {isAiReplying && (
                <div className="flex items-start gap-3">
                    <UserAvatar user={usersCache.get(AI_USER_ID)!} className="h-8 w-8" />
                    <div className="bg-card/80 backdrop-blur-sm rounded-xl px-4 py-2 rounded-tl-none flex items-center gap-2">
                    <span className="h-2 w-2 bg-muted-foreground rounded-full animate-pulse delay-0"></span>
                    <span className="h-2 w-2 bg-muted-foreground rounded-full animate-pulse delay-150"></span>
                    <span className="h-2 w-2 bg-muted-foreground rounded-full animate-pulse delay-300"></span>
                    </div>
                </div>
                )}
            </motion.div>
        </ScrollArea>
    );
});
MessageList.displayName = 'MessageList';

src/components/mobile-chat-layout.tsx.content:
'use client';
import { ChatView } from './chat-view';
import { useAppShell } from './app-shell';
import { ChatList } from './chat-list';
import React from 'react';
import { useMobileKeyboardHeight } from '@/hooks/use-mobile-keyboard-height';

export function MobileChatLayout() {
  const {
    selectedChat,
    isAiReplying,
    currentUser,
    handleBack,
    messages,
    loadMoreMessages,
    hasMoreMessages,
    isLoadingMore,
  } = useAppShell();

  // Dynamically set height to visible viewport (accounts for keyboard)
  const { viewportHeight } = useMobileKeyboardHeight();

  return (
    <div
      className="flex flex-col w-full h-full overflow-hidden z-10"
      style={{ height: viewportHeight }}
    >
      {/* Only render one main view at a time */}
      {!selectedChat ? (
        <div className="flex-1 min-h-0">
          <ChatList />
        </div>
      ) : (
        <div className="flex-1 min-h-0">
          <ChatView
            chat={selectedChat}
            isAiReplying={isAiReplying}
            currentUser={currentUser}
            onBack={handleBack}
            messages={messages}
            loadMoreMessages={loadMoreMessages}
            hasMoreMessages={hasMoreMessages}
            isLoadingMore={isLoadingMore}
          />
        </div>
      )}
    </div>
  );
}

src/components/mobile-galaxy-background..content:
#mobile-galaxy-canvas {
  position: fixed;
  inset: 0;
  width: 100%;
  height: 100%;
  z-index: 0;
  pointer-events: none;
  background-color: transparent;
}

'use client';
import React, { useRef, useEffect } from 'react';

const isReducedMotion = () =>
  typeof window !== 'undefined' && window.matchMedia('(prefers-reduced-motion: reduce)').matches;

type Star = {
  x: number;
  y: number;
  size: number;
  speedX: number;
  speedY: number;
  opacity: number;
  opacityDirection: number;
};

type ShootingStar = {
  x: number;
  y: number;
  len: number;
  speed: number;
  size: number;
  active: boolean;
};

type Nebula = {
  x: number;
  y: number;
  radius: number;
  color: string;
  opacity: number;
  opacityDirection: number;
};

export function MobileGalaxyBackground() {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const animationFrameId = useRef<number | null>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas || isReducedMotion()) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    let stars: Star[] = [];
    let shootingStars: ShootingStar[] = [];
    let nebulas: Nebula[] = [];

    const setup = () => {
      const dpr = window.devicePixelRatio || 1;
      const vw = window.visualViewport ? window.visualViewport.width : window.innerWidth;
      const vh = window.visualViewport ? window.visualViewport.height : window.innerHeight;

      canvas.width = vw * dpr;
      canvas.height = vh * dpr;
      canvas.style.width = `${vw}px`;
      canvas.style.height = `${vh}px`;
      ctx.scale(dpr, dpr);

      stars = [];
      shootingStars = [];
      nebulas = [];

      // Create stars
      for (let i = 0; i < 300; i++) {
        stars.push({
          x: Math.random() * vw,
          y: Math.random() * vh,
          size: Math.random() * 1.2 + 0.3,
          speedX: (Math.random() - 0.5) * 0.1,
          speedY: (Math.random() * 0.15 + 0.1) * -1, // Move upwards
          opacity: Math.random() * 0.5 + 0.3,
          opacityDirection: Math.random() > 0.5 ? 1 : -1,
        });
      }

      // Create shooting stars
      for (let i = 0; i < 2; i++) {
        shootingStars.push({
            x: Math.random() * vw,
            y: Math.random() * vh,
            len: Math.random() * 80 + 10,
            speed: Math.random() * 8 + 4,
            size: Math.random() * 1 + 0.5,
            active: false
        });
      }

      // Create nebulas
      nebulas.push({ x: vw * 0.2, y: vh * 0.3, radius: vw * 0.8, color: 'hsla(260, 65%, 30%, 0.4)', opacity: 0.4, opacityDirection: 1 });
      nebulas.push({ x: vw * 0.8, y: vh * 0.8, radius: vw * 0.7, color: 'hsla(220, 70%, 40%, 0.3)', opacity: 0.3, opacityDirection: -1 });
      nebulas.push({ x: vw * 0.1, y: vh * 0.9, radius: vw * 0.6, color: 'hsla(320, 70%, 40%, 0.25)', opacity: 0.25, opacityDirection: 1 });

    };

    const draw = () => {
        if (!ctx || !canvas) return;
        const dpr = window.devicePixelRatio || 1;
        const vw = window.visualViewport ? window.visualViewport.width : window.innerWidth;
        const vh = window.visualViewport ? window.visualViewport.height : window.innerHeight;

        canvas.width = vw * dpr;
        canvas.height = vh * dpr;
        canvas.style.width = `${vw}px`;
        canvas.style.height = `${vh}px`;
        ctx.scale(dpr, dpr);

        ctx.clearRect(0, 0, vw, vh);
        ctx.fillStyle = '#000005';
        ctx.fillRect(0, 0, vw, vh);

        // Draw nebulas first
        nebulas.forEach(n => {
            const grad = ctx.createRadialGradient(n.x, n.y, 0, n.x, n.y, n.radius);
            const color = n.color.replace(/, ([\d.]+)\)/, `, ${n.opacity})`);
            grad.addColorStop(0, color);
            grad.addColorStop(1, 'transparent');
            ctx.fillStyle = grad;
            ctx.fillRect(0, 0, vw, vh);
        });

        // Draw stars on top
        stars.forEach(star => {
            ctx.beginPath();
            ctx.arc(star.x, star.y, star.size, 0, Math.PI * 2);
            ctx.fillStyle = `hsla(200, 100%, 95%, ${star.opacity})`;
            ctx.fill();
        });

        // Draw shooting stars on top
        shootingStars.forEach(s => {
            if (s.active) {
                ctx.beginPath();
                ctx.moveTo(s.x, s.y);
                ctx.lineTo(s.x - s.len, s.y + s.len);
                ctx.strokeStyle = 'white';
                ctx.lineWidth = s.size;
                ctx.stroke();
            }
        });
    };

    const update = () => {
      if(!canvas) return;
      const vw = window.visualViewport ? window.visualViewport.width : window.innerWidth;
      const vh = window.visualViewport ? window.visualViewport.height : window.innerHeight;
      // Update stars
      stars.forEach(star => {
        star.x += star.speedX;
        star.y += star.speedY;

        // Twinkle effect
        star.opacity += 0.003 * star.opacityDirection;
        if (star.opacity <= 0.2 || star.opacity >= 0.8) {
          star.opacityDirection *= -1;
        }

        if (star.y < 0) {
            star.y = vh;
            star.x = Math.random() * vw;
        }
        if (star.x < 0) star.x = vw;
        if (star.x > vw) star.x = 0;
      });

      // Update shooting stars
       shootingStars.forEach(s => {
            if (s.active) {
                s.x -= s.speed;
                s.y += s.speed;
                if (s.x < 0 || s.y > vh) {
                    s.active = false;
                }
            } else if (Math.random() < 0.001) {
                s.active = true;
                s.x = Math.random() * vw / 2 + vw / 2;
                s.y = Math.random() * vh / 2;
                s.len = Math.random() * 80 + 10;
                s.speed = Math.random() * 8 + 4;
                s.size = Math.random() * 1 + 0.5;
            }
        });

        // Update nebulas
        nebulas.forEach(n => {
            n.opacity += 0.001 * n.opacityDirection;
            if(n.opacity > 0.6 || n.opacity < 0.2) n.opacityDirection *= -1;
        });

    };

    const animate = () => {
      update();
      draw();
      animationFrameId.current = requestAnimationFrame(animate);
    };

    const handleResize = () => {
      setup();
      draw(); // Redraw immediately on resize
    };

    setup();
    animate();
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
      if (animationFrameId.current) {
        cancelAnimationFrame(animationFrameId.current);
      }
    };
  }, []);

  if (isReducedMotion()) {
      return <div className="fixed inset-0 bg-black -z-10" />;
  }

  return <canvas ref={canvasRef} id="mobile-galaxy-canvas" className="fixed inset-0 -z-10" aria-hidden="true"></canvas>;
}

src/components/new-chat-dialog.tsx.content:
'use client';

import { useState, useMemo, useCallback } from 'react';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import { Button } from './ui/button';
import { Input } from './ui/input';
import { User } from '@/lib/types';
import { ScrollArea } from './ui/scroll-area';
import { UserAvatar } from './user-avatar';
import { Search, Loader2 } from 'lucide-react';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './ui/tabs';
import { Checkbox } from './ui/checkbox';
import { Label } from './ui/label';
import { collection, query, where, getDocs, limit } from 'firebase/firestore';
import { db } from '@/lib/firebase';
import { useToast } from '@/hooks/use-toast';

interface NewChatDialogProps {
  users: User[];
  onCreateChat: (user: User) => void;
  onCreateGroupChat: (groupName: string, users: User[]) => void;
  children: React.ReactNode;
  currentUser?: User;
}

const isEmail = (str: string) => {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(str);
}

export function NewChatDialog({ users, onCreateChat, onCreateGroupChat, children, currentUser }: NewChatDialogProps) {
  const [isOpen, setIsOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<User[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const { toast } = useToast();

  const [selectedUsers, setSelectedUsers] = useState<User[]>([]);
  const [groupName, setGroupName] = useState('');

  const publicUsers = useMemo(() => users.filter(u => u.uid !== currentUser?.uid), [users, currentUser]);

  const handleSearch = useCallback(async (term: string) => {
    if (!term.trim()) {
        setSearchResults([]);
        setIsSearching(false);
        return;
    }
    setIsSearching(true);
    try {
        // Check if Firebase is properly configured (not using demo values)
        const isFirebaseConfigured = process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID && 
                                    process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID !== 'demo-project';

        if (isEmail(term) && isFirebaseConfigured) {
            // Try Firebase search for email if properly configured
            const usersRef = collection(db, 'users');
            const q = query(usersRef, where('email', '==', term.toLowerCase()), limit(1));
            const querySnapshot = await getDocs(q);
            const foundUsers = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() } as User));

            if (querySnapshot.empty) {
               toast({ title: "User not found", description: "No user found with that exact email address.", variant: "destructive" });
            }
            setSearchResults(foundUsers);
        } else {
            // Fallback to client-side filtering for both email and name searches
            const filtered = publicUsers.filter(user => {
                const searchLower = term.toLowerCase();
                return (user.name && user.name.toLowerCase().includes(searchLower)) ||
                       (user.email && user.email.toLowerCase().includes(searchLower));
            });
            setSearchResults(filtered);

            if (filtered.length === 0) {
                toast({ 
                    title: "No results", 
                    description: isEmail(term) ? 
                        "No user found with that email address." : 
                        "No users found matching your search.", 
                    variant: "default" 
                });
            }
        }
    } catch (error) {
        console.error("Error searching users:", error);
        // Fallback to client-side search if Firebase fails
        const filtered = publicUsers.filter(user => {
            const searchLower = term.toLowerCase();
            return (user.name && user.name.toLowerCase().includes(searchLower)) ||
                   (user.email && user.email.toLowerCase().includes(searchLower));
        });
        setSearchResults(filtered);

        toast({ 
            title: "Search completed", 
            description: `Found ${filtered.length} users in local search.`, 
            variant: "default" 
        });
    } finally {
        setIsSearching(false);
    }
  }, [publicUsers, toast]);


  const displayedUsers = searchTerm ? searchResults : publicUsers;

  const handleCreateChatClick = (user: User) => {
    if (!currentUser) {
        toast({ title: "Error", description: "Cannot create chat. Current user not found.", variant: "destructive" });
        return;
    }
    onCreateChat(user);
    setIsOpen(false);
    resetState();
  };

  const handleCreateGroup = () => {
    if (groupName.trim() && selectedUsers.length > 0) {
        onCreateGroupChat(groupName.trim(), selectedUsers);
        setIsOpen(false);
        resetState();
    }
  }

  const handleUserSelection = (user: User, isSelected: boolean) => {
    if (isSelected) {
        setSelectedUsers(prev => [...prev, user]);
    } else {
        setSelectedUsers(prev => prev.filter(u => u.id !== user.id));
    }
  }

  const resetState = () => {
    setSearchTerm('');
    setSearchResults([]);
    setGroupName('');
    setSelectedUsers([]);
  }

  const onSearchTermChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const term = e.target.value;
    setSearchTerm(term);
    handleSearch(term);
  }

  return (
    <Dialog open={isOpen} onOpenChange={(open) => {
        setIsOpen(open);
        if (!open) resetState();
    }}>
      <DialogTrigger asChild>{children}</DialogTrigger>
      <DialogContent className="sm:max-w-md">
        <DialogHeader>
          <DialogTitle>Start a new chat</DialogTitle>
        </DialogHeader>
        <Tabs defaultValue="private" className="w-full">
            <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="private">Private</TabsTrigger>
                <TabsTrigger value="group">Group</TabsTrigger>
            </TabsList>
            <div className="relative my-4">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                    placeholder="Search by name or exact email..."
                    value={searchTerm}
                    onChange={onSearchTermChange}
                    className="pl-10"
                />
                 {isSearching && isEmail(searchTerm) && <Loader2 className="absolute right-3 top-1/2 -translate-y-1/2 h-4 w-4 animate-spin" />}
            </div>

            <TabsContent value="private">
                <ScrollArea className="h-72">
                  <div className="p-1">
                    {displayedUsers.length > 0 ? (
                      displayedUsers.map(user => (
                        <div
                          key={user.id}
                          className="flex items-center justify-between p-2 rounded-lg hover:bg-muted/50 cursor-pointer"
                          onClick={() => handleCreateChatClick(user)}
                        >
                          <div className="flex items-center gap-3">
                            <UserAvatar user={user} className="h-10 w-10" />
                            <div>
                              <p className="font-semibold">{user.name}</p>
                              <p className="text-sm text-muted-foreground">{user.email || 'No email'}</p>
                            </div>
                          </div>
                        </div>
                      ))
                    ) : (
                      <p className="text-center text-muted-foreground p-4">
                        {searchTerm && !isSearching ? 'No users found.' : 'Type to search for users.'}
                        </p>
                    )}
                  </div>
                </ScrollArea>
            </TabsContent>

            <TabsContent value="group">
                <div className="space-y-4">
                    <div>
                        <Label htmlFor="group-name">Group Name</Label>
                        <Input 
                            id="group-name"
                            placeholder="Enter a name for your group"
                            value={groupName}
                            onChange={(e) => setGroupName(e.target.value)}
                        />
                    </div>
                     <p className="text-sm font-medium text-muted-foreground">Select members ({selectedUsers.length})</p>
                    <ScrollArea className="h-56">
                        <div className="p-1 space-y-1">
                            {displayedUsers.length > 0 ? (
                            displayedUsers.map(user => (
                                <div key={user.id} className="flex items-center p-2 rounded-lg hover:bg-muted/50">
                                    <Checkbox 
                                        id={`user-${user.id}`}
                                        className="mr-3"
                                        onCheckedChange={(checked) => handleUserSelection(user, !!checked)}
                                        checked={selectedUsers.some(u => u.id === user.id)}
                                    />
                                    <Label htmlFor={`user-${user.id}`} className="flex items-center gap-3 cursor-pointer flex-1">
                                        <UserAvatar user={user} className="h-10 w-10" />
                                        <div>
                                            <p className="font-semibold">{user.name}</p>
                                            <p className="text-sm text-muted-foreground">{user.email || 'No email'}</p>
                                        </div>
                                    </Label>
                                </div>
                            ))
                            ) : (
                            <p className="text-center text-muted-foreground p-4">
                                {searchTerm && !isSearching ? 'No users found.' : 'Type to search for users.'}
                            </p>
                            )}
                        </div>
                    </ScrollArea>
                </div>
            </TabsContent>
        </Tabs>
        <DialogFooter>
          <Button variant="outline" onClick={() => setIsOpen(false)}>
            Cancel
          </Button>
           <Button onClick={handleCreateGroup} disabled={groupName.trim().length === 0 || selectedUsers.length === 0}>
            Create Group
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

