src/components/stories-tray.tsx.content:
'use client';
import { useMemo } from 'react';
import { Plus } from 'lucide-react';
import { User, Story } from '@/lib/types';
import { UserAvatar } from './user-avatar';
import { ScrollArea, ScrollBar } from './ui/scroll-area';
import { cn } from '@/lib/utils';
import React from 'react';

interface StoriesTrayProps {
    currentUser?: User;
    stories: Story[];
    usersWithStories: User[];
    onViewStory: (user: User, stories: Story[]) => void;
    onCreateStory: () => void;
}

export function StoriesTray({ 
    currentUser, 
    stories, 
    usersWithStories, 
    onViewStory, 
    onCreateStory,
}: StoriesTrayProps) {

    const storiesByUser = useMemo(() => {
        const userStoryMap = new Map<string, Story[]>();
        stories.forEach(story => {
            if (!userStoryMap.has(story.ownerId)) {
                userStoryMap.set(story.ownerId, []);
            }
            userStoryMap.get(story.ownerId)!.push(story);
        });

        // Sort stories by createdAt date
        userStoryMap.forEach(userStories => {
            userStories.sort((a, b) => b.createdAt.toMillis() - a.createdAt.toMillis());
        });

        return userStoryMap;
    }, [stories]);

    const hasUnreadStory = (userStories: Story[]) => {
        return userStories.some(story => !story.viewedBy.includes(currentUser?.uid || ''));
    };

    if (!currentUser) return null;

    const myStories = storiesByUser.get(currentUser.uid) || [];
    const hasMyStory = myStories.length > 0;
    const hasMyUnreadStory = hasMyStory && hasUnreadStory(myStories);

    const friendsWithStories = usersWithStories.filter(u => u.uid !== currentUser.uid);

    return (
        <>
            <div className="p-4 border-b border-border/50 group-[[data-sidebar-state=collapsed]]/sidebar:hidden">
                <ScrollArea className="w-full whitespace-nowrap">
                    <div className="flex gap-4">
                        <div className="flex flex-col items-center gap-2">
                             <button 
                                onClick={hasMyStory ? () => onViewStory(currentUser, myStories) : onCreateStory} 
                                className="relative h-16 w-16"
                            >
                                <div className={cn(
                                    "rounded-full p-0.5 border-2",
                                    hasMyUnreadStory ? "border-primary" : "border-transparent"
                                )}>
                                    <UserAvatar user={currentUser} className="h-[58px] w-[58px]" />
                                </div>
                                {!hasMyStory && (
                                    <div className="absolute bottom-0 right-0 bg-primary text-primary-foreground rounded-full p-1 border-2 border-background">
                                        <Plus className="h-3 w-3" />
                                    </div>
                                )}
                            </button>
                            <p className="text-xs font-medium truncate w-16">Your Story</p>
                        </div>

                        {friendsWithStories.map(user => {
                            const userStories = storiesByUser.get(user.uid) || [];
                            if (userStories.length === 0) return null;

                            const hasUnread = hasUnreadStory(userStories);

                            return (
                                <div key={user.uid} className="flex flex-col items-center gap-2">
                                    <button 
                                        onClick={() => onViewStory(user, userStories)}
                                        className={cn(
                                            "rounded-full p-0.5 border-2",
                                            hasUnread ? "border-primary" : "border-muted"
                                        )}
                                    >
                                        <UserAvatar user={user} className="h-16 w-16" />
                                    </button>
                                    <p className="text-xs font-medium truncate w-16">{user.name}</p>
                                </div>
                            )
                        })}
                    </div>
                    <ScrollBar orientation="horizontal" />
                 </ScrollArea>
                 {!hasMyStory && friendsWithStories.length === 0 && (
                    <p className="text-sm text-muted-foreground text-center py-4">No stories yet</p>
                )}
            </div>
        </>
    );
}

src/components/story-uploader.tsx.content:
'use client';

import { useState, useRef, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from './ui/dialog';
import { Button } from './ui/button';
import { Textarea } from './ui/textarea';
import Image from 'next/image';
import { Loader2, PlusCircle, X } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import imageCompression from 'browser-image-compression';

interface StoryUploaderProps {
    isOpen: boolean;
    onOpenChange: (isOpen: boolean) => void;
    onCreateStory: (mediaFile: File, caption?: string) => Promise<void>;
}

export function StoryUploader({ isOpen, onOpenChange, onCreateStory }: StoryUploaderProps) {
    const [file, setFile] = useState<File | null>(null);
    const [previewUrl, setPreviewUrl] = useState<string | null>(null);
    const [caption, setCaption] = useState('');
    const [isUploading, setIsUploading] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);
    const { toast } = useToast();

    useEffect(() => {
        if (!isOpen) {
            // Reset state when dialog closes
            setFile(null);
            setPreviewUrl(null);
            setCaption('');
            setIsUploading(false);
            setError(null);
        }
    }, [isOpen]);

    const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        const selectedFile = event.target.files?.[0];
        if (selectedFile) {
            if (!selectedFile.type.startsWith('image/')) {
                toast({
                    title: "Invalid File Type",
                    description: "Currently, only images are supported for stories.",
                    variant: "destructive"
                });
                return;
            }
            setFile(selectedFile);
            setPreviewUrl(URL.createObjectURL(selectedFile));
            setError(null);
        }
    };

    const handlePostStory = async () => {
        if (!file) return;

        setIsUploading(true);
        setError(null);

        try {
            const options = {
                maxSizeMB: 1.5,
                maxWidthOrHeight: 1920,
                useWebWorker: true,
            };
            const compressedFile = await imageCompression(file, options);

            await onCreateStory(compressedFile, caption);

            toast({
                title: "Success!",
                description: "Your story has been posted.",
            });
            onOpenChange(false);

        } catch (err) {
            console.error('Story upload failed:', err);
            setError('Upload failed. Please try again.');
            toast({
                title: 'Upload Failed',
                description: 'There was a problem posting your story.',
                variant: 'destructive',
            });
        } finally {
            setIsUploading(false);
        }
    };

    return (
        <Dialog open={isOpen} onOpenChange={onOpenChange}>
            <DialogContent>
                <DialogHeader>
                    <DialogTitle>Create a new story</DialogTitle>
                </DialogHeader>
                <div className="space-y-4 py-4">
                    {!previewUrl ? (
                        <div
                            className="flex flex-col items-center justify-center p-8 border-2 border-dashed rounded-lg cursor-pointer"
                            onClick={() => fileInputRef.current?.click()}
                        >
                            <PlusCircle className="h-12 w-12 text-muted-foreground" />
                            <p className="mt-2 text-sm text-muted-foreground">Click to select an image</p>
                            <input
                                type="file"
                                ref={fileInputRef}
                                onChange={handleFileChange}
                                className="hidden"
                                accept="image/*"
                            />
                        </div>
                    ) : (
                        <div className="relative">
                            <Image
                                src={previewUrl}
                                alt="Story preview"
                                width={500}
                                height={500}
                                className="rounded-lg object-contain max-h-[50vh]"
                            />
                            <Button
                                variant="destructive"
                                size="icon"
                                className="absolute top-2 right-2 rounded-full h-7 w-7"
                                onClick={() => {
                                    setFile(null);
                                    setPreviewUrl(null);
                                }}
                            >
                                <X className="h-4 w-4" />
                            </Button>
                        </div>
                    )}
                    <Textarea
                        placeholder="Add a caption... (optional)"
                        value={caption}
                        onChange={(e) => setCaption(e.target.value)}
                        disabled={isUploading || !file}
                    />
                     {error && (
                        <div className="text-destructive text-sm flex items-center justify-between">
                            <span>{error}</span>
                            <Button variant="link" onClick={handlePostStory}>Retry</Button>
                        </div>
                    )}
                </div>
                <DialogFooter>
                    <Button variant="outline" onClick={() => onOpenChange(false)} disabled={isUploading}>
                        Cancel
                    </Button>
                    <Button onClick={handlePostStory} disabled={!file || isUploading}>
                        {isUploading && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
                        {isUploading ? 'Posting...' : 'Post Story'}
                    </Button>
                </DialogFooter>
            </DialogContent>
        </Dialog>
    );
}

