    return output!;
  }
);

src/ai/flows/weather-flow.ts.content:
'use server';
/**
 * @fileOverview A flow for getting the current weather.
 *
 * - getWeather - A function that gets the weather for a given location.
 * - GetWeatherInput - The input type for the getWeather function.
 * - GetWeatherOutput - The return type for the getWeather function.
 */

import { ai } from '@/ai/genkit';
import { z } from 'zod';

const GetWeatherInputSchema = z.object({
  location: z.string().describe('The location to get the weather for.'),
  unit: z.enum(['Celsius', 'Fahrenheit']).optional().describe('The unit for the temperature. Defaults to Celsius.')
});
export type GetWeatherInput = z.infer<typeof GetWeatherInputSchema>;

const GetWeatherOutputSchema = z.object({
  temperature: z.number().describe('The current temperature in the requested unit.'),
  condition: z.enum(['Sunny', 'Cloudy', 'Rainy', 'Windy', 'Stormy', 'Snowy', 'Clear', 'Mist', 'Haze', 'Fog']).describe('The current weather condition.'),
  unit: z.enum(['Celsius', 'Fahrenheit']).describe('The unit of the provided temperature.'),
});
export type GetWeatherOutput = z.infer<typeof GetWeatherOutputSchema>;


const weatherPrompt = ai.definePrompt({
    name: 'weatherPrompt',
    input: { schema: GetWeatherInputSchema },
    output: { schema: GetWeatherOutputSchema },
    prompt: `You are a weather service. The user will provide a location.
    Return the current temperature in the specified unit (default to Celsius if not provided) and the current weather condition for that location.

    Location: {{{location}}}
    Unit: {{{unit}}}

    Please provide the weather information in the specified JSON format. If the condition is not in the provided enum, choose the closest match.
    For example, for "Partly Cloudy", use "Cloudy". For "Clear sky", use "Clear" if available, otherwise "Sunny".
    `,
});


const getWeatherFlow = ai.defineFlow(
  {
    name: 'getWeatherFlow',
    inputSchema: GetWeatherInputSchema,
    outputSchema: GetWeatherOutputSchema,
  },
  async (input) => {
    try {
      const { output } = await weatherPrompt(input);
      if (!output) {
        throw new Error('Unable to get weather information.');
      }

      const validConditions = GetWeatherOutputSchema.shape.condition.options;
      if (!validConditions.includes(output.condition)) {
        console.warn(`Model returned an invalid condition: "${output.condition}". Falling back to "Cloudy".`);
        output.condition = 'Cloudy'; 
      }
      return output;
    } catch (error) {
        console.warn("AI weather call failed, returning mock data. Error:", error);
        // This is a fallback for when the API limit is hit or another error occurs.
        return {
            temperature: input.unit === 'Fahrenheit' ? 68 : 20,
            condition: 'Sunny',
            unit: input.unit || 'Celsius',
        };
    }
  }
);


export async function getWeather(input: GetWeatherInput): Promise<GetWeatherOutput> {
  return getWeatherFlow(input);
}

src/app/(auth)/auth-background.css.content:
/* Static background for reduced motion or fallback */
.auth-bg {
    background-color: #0c0a1e; /* Deep indigo base */
    background-image: radial-gradient(at 20% 80%, hsla(255, 60%, 30%, 0.3) 0px, transparent 50%),
                      radial-gradient(at 80% 10%, hsla(280, 50%, 40%, 0.25) 0px, transparent 50%);
    overflow: hidden;
    position: relative;
}

.blob-container {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    overflow: hidden;
    z-index: 0;
}

/* Base styles for all blobs */
.blob-container::before,
.blob-container::after,
.blob-container > div::before,
.blob-container > div::after {
    content: '';
    position: absolute;
    border-radius: 50%;
    will-change: transform;
    opacity: 0;
    mix-blend-mode: screen; /* Soft additive blending */
    filter: blur(80px); /* Increased blur for softer edges */
}

/* Blob 1 (Violet) */
.blob-container::before {
    width: 550px; /* Increased size */
    height: 550px;
    background: hsla(283, 60%, 55%, 0.25); /* Increased opacity and vibrancy */
    animation: blob-float 15s infinite cubic-bezier(0.68, -0.55, 0.27, 1.55);
}

/* Blob 2 (Cyan Accent) */
.blob-container::after {
    width: 450px; /* Increased size */
    height: 450px;
    background: hsla(180, 80%, 50%, 0.2); /* Increased opacity */
    animation: blob-float 20s infinite cubic-bezier(0.68, -0.55, 0.27, 1.55) 3s;
}

/* Blob 3 (Indigo) */
.blob-container > div::before {
    width: 500px; /* Increased size */
    height: 500px;
    background: hsla(260, 80%, 65%, 0.28); /* Increased opacity */
    animation: blob-float 18s infinite cubic-bezier(0.68, -0.55, 0.27, 1.55) 1.5s;
}

/* Blob 4 (Deeper Violet) */
.blob-container > div::after {
    width: 400px; /* Increased size */
    height: 400px;
    background: hsla(275, 50%, 45%, 0.22); /* Increased opacity */
    animation: blob-float 25s infinite cubic-bezier(0.68, -0.55, 0.27, 1.55) 5s;
}


@keyframes blob-float {
    0% {
        transform: translate(0, 0) scale(1);
        opacity: 0.15;
    }
    33% {
        transform: translate(calc(60vw - 40%), calc(70vh - 60%)) scale(1.3);
        opacity: 0.3;
    }
    66% {
        transform: translate(calc(25vw - 50%), calc(40vh - 50%)) scale(0.9);
        opacity: 0.25;
    }
    100% {
        transform: translate(0, 0) scale(1);
        opacity: 0.15;
    }
}

/* Apply different initial positions and delays */
.blob-container::after {
    animation-delay: -5s;
}
.blob-container > div::before {
    animation-delay: -10s;
}
.blob-container > div::after {
    animation-delay: -2.5s;
    animation-direction: alternate-reverse;
}


/* Accessibility: Disable animations if the user prefers reduced motion */
@media (prefers-reduced-motion: reduce) {
    .blob-container::before,
    .blob-container::after,
    .blob-container > div::before,
    .blob-container > div::after {
        animation: none;
        opacity: 0; /* Hide the animated blobs completely */
    }
}

